mapper.insert:
INSERT INTO BATTER_RECORD
(
    YEAR
    , MONTH
    , DATE
    , GAME_SEQ
    , NAME
    , PLATE_APPEARS
    , AT_BATS
    , HITS
    , SINGLES
    , DOUBLES
    , TRIPLES
    , HOME_RUNS
    , RUNS_SCORED
    , RUNS_BATTED_IN
    , BASES_ON_BALLS
    , STRIKE_OUTS
    , STOLEN_BASES
)
VALUES
(
    #{year}
    , #{month}
    , #{date}
    , #{gameSeq}
    , #{name}
    , #{plateAppears}
    , #{atBats}
    , #{hits}
    , #{singles}
    , #{doubles}
    , #{triples}
    , #{homeRuns}
    , #{runsScored}
    , #{runsBattedIn}
    , #{basesOnBalls}
    , #{strikeOuts}
    , #{stolenBases}
)

mapper.delete:
delete from BATTER_RECORD
where   year = #{year}
and     month = #{month}
and     date = #{date}
and     game_seq = #{gameSeq}
and     name = #{name}
